mapper.update:
UPDATE TBL_BOARD
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}
WHERE BOARD_NUMBER = #{boardNumber}

mapper.select:
SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, USER_NUMBER, USER_NICKNAME, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, USER_FILE_UPLOAD_PATH, USER_FILE_UUID, USER_FILE_NAME, BOARD_CATEGORY_NUMBER, (SELECT COUNT(L.USER_NUMBER) FROM TBL_BOARD_LIKE L WHERE L.USER_NUMBER = 1 AND S2.BOARD_NUMBER = L.BOARD_NUMBER) LIKE_CNT, (SELECT COUNT(BOARD_REPLY_NUMBER) FROM TBL_BOARD_REPLY BR WHERE S2.BOARD_NUMBER = BR.BOARD_NUMBER) REPLY_CNT FROM( SELECT ROWNUM AS RNUM, BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, USER_NUMBER, USER_NICKNAME, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, USER_FILE_UPLOAD_PATH, USER_FILE_UUID, USER_FILE_NAME, BOARD_CATEGORY_NUMBER FROM( SELECT B.BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, B.USER_NUMBER, USER_NICKNAME, J.BOARD_FILE_UPLOAD_PATH, J.BOARD_FILE_UUID, J.BOARD_FILE_NAME, UF.USER_FILE_UPLOAD_PATH, UF.USER_FILE_UUID, UF.USER_FILE_NAME, B.BOARD_CATEGORY_NUMBER FROM TBL_USER U JOIN TBL_BOARD B ON U.USER_NUMBER = B.USER_NUMBER LEFT JOIN ( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER FROM( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER, RANK() OVER(PARTITION BY BOARD_NUMBER ORDER BY BOARD_FILE_NUMBER) RK FROM TBL_BOARD_FILE ) WHERE RK = 1 ) J ON B.BOARD_NUMBER = J.BOARD_NUMBER LEFT JOIN TBL_USER_FILE UF ON U.USER_NUMBER = UF.USER_NUMBER ORDER BY B.BOARD_NUMBER DESC ) S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, USER_NUMBER, USER_NICKNAME, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, USER_FILE_UPLOAD_PATH, USER_FILE_UUID, USER_FILE_NAME, BOARD_CATEGORY_NUMBER, (SELECT COUNT(BOARD_REPLY_NUMBER) FROM TBL_BOARD_REPLY BR WHERE S2.BOARD_NUMBER = BR.BOARD_NUMBER) REPLY_CNT FROM ( SELECT ROWNUM AS RNUM, BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, USER_NUMBER, USER_NICKNAME, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, USER_FILE_UPLOAD_PATH, USER_FILE_UUID, USER_FILE_NAME, BOARD_CATEGORY_NUMBER FROM ( SELECT B.BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, B.USER_NUMBER, USER_NICKNAME, J.BOARD_FILE_UPLOAD_PATH, J.BOARD_FILE_UUID, J.BOARD_FILE_NAME, UF.USER_FILE_UPLOAD_PATH, UF.USER_FILE_UUID, UF.USER_FILE_NAME, B.BOARD_CATEGORY_NUMBER FROM TBL_USER U JOIN TBL_BOARD B ON U.USER_NUMBER = B.USER_NUMBER LEFT JOIN ( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER FROM ( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER, RANK() OVER (PARTITION BY BOARD_NUMBER ORDER BY BOARD_FILE_NUMBER) RK FROM TBL_BOARD_FILE ) WHERE RK = 1 ) J ON B.BOARD_NUMBER = J.BOARD_NUMBER LEFT JOIN TBL_USER_FILE UF ON U.USER_NUMBER = UF.USER_NUMBER
BOARD_TITLE LIKE '%' || #{search.keyword} || '%'
BOARD_CONTENT LIKE '%' || #{search.keyword} || '%'
ORDER BY B.BOARD_NUMBER DESC ) S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, USER_NUMBER, USER_NICKNAME, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, USER_FILE_UPLOAD_PATH, USER_FILE_UUID, USER_FILE_NAME, BOARD_CATEGORY_NUMBER FROM ( SELECT ROWNUM AS RNUM, B.BOARD_NUMBER, B.BOARD_TITLE, B.BOARD_VIEW_CNT, B.BOARD_CONTENT, B.USER_NUMBER, U.USER_NICKNAME, J.BOARD_FILE_UPLOAD_PATH, J.BOARD_FILE_UUID, J.BOARD_FILE_NAME, UF.USER_FILE_UPLOAD_PATH, UF.USER_FILE_UUID, UF.USER_FILE_NAME, B.BOARD_CATEGORY_NUMBER FROM TBL_BOARD B JOIN TBL_USER U ON B.USER_NUMBER = U.USER_NUMBER LEFT JOIN ( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER FROM ( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER, RANK() OVER (PARTITION BY BOARD_NUMBER ORDER BY BOARD_FILE_NUMBER) RK FROM TBL_BOARD_FILE ) WHERE RK = 1 ) J ON B.BOARD_NUMBER = J.BOARD_NUMBER LEFT JOIN TBL_USER_FILE UF ON U.USER_NUMBER = UF.USER_NUMBER
AND B.BOARD_CATEGORY_NUMBER = #{boardCategoryNumber}
ORDER BY B.BOARD_NUMBER DESC ) S
WHERE RNUM <= #{criteria.page} * #{criteria.amount}
AND RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, USER_NUMBER, USER_NICKNAME, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, USER_FILE_UPLOAD_PATH, USER_FILE_UUID, USER_FILE_NAME, BOARD_CATEGORY_NUMBER, (SELECT COUNT(L.USER_NUMBER) FROM TBL_BOARD_LIKE L WHERE L.USER_NUMBER = 1 AND S2.BOARD_NUMBER = L.BOARD_NUMBER) LIKE_CNT, (SELECT COUNT(BOARD_REPLY_NUMBER) FROM TBL_BOARD_REPLY BR WHERE S2.BOARD_NUMBER = BR.BOARD_NUMBER) REPLY_CNT FROM ( SELECT ROWNUM AS RNUM, BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, USER_NUMBER, USER_NICKNAME, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, USER_FILE_UPLOAD_PATH, USER_FILE_UUID, USER_FILE_NAME, BOARD_CATEGORY_NUMBER FROM ( SELECT B.BOARD_NUMBER, BOARD_TITLE, BOARD_VIEW_CNT, BOARD_CONTENT, B.USER_NUMBER, USER_NICKNAME, J.BOARD_FILE_UPLOAD_PATH, J.BOARD_FILE_UUID, J.BOARD_FILE_NAME, UF.USER_FILE_UPLOAD_PATH, UF.USER_FILE_UUID, UF.USER_FILE_NAME, B.BOARD_CATEGORY_NUMBER FROM TBL_USER U JOIN TBL_BOARD B ON U.USER_NUMBER = B.USER_NUMBER LEFT JOIN ( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER FROM ( SELECT BOARD_FILE_NUMBER, BOARD_FILE_UPLOAD_PATH, BOARD_FILE_UUID, BOARD_FILE_NAME, BOARD_NUMBER, RANK() OVER (PARTITION BY BOARD_NUMBER ORDER BY BOARD_FILE_NUMBER) RK FROM TBL_BOARD_FILE ) WHERE RK = 1 ) J ON B.BOARD_NUMBER = J.BOARD_NUMBER LEFT JOIN TBL_USER_FILE UF ON U.USER_NUMBER = UF.USER_NUMBER
AND BOARD_CATEGORY_NUMBER = #{searchVo.boardCategoryNumber}
AND BOARD_TITLE LIKE '%' || #{searchVo.keyword} || '%'
AND USER_NICKNAME LIKE '%' || #{searchVo.keyword} || '%'
ORDER BY B.BOARD_NUMBER DESC ) S1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) S2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}
